mapper.select:
SELECT mp_corpnum, mp_corpname, mp_corpbn, mp_bizperson, mp_bizphone, mp_useyn, mp_insertdate FROM mplan_company
AND ${searchColumn} LIKE CONCAT('',#{searchText},'%')
ORDER BY mp_insertdate DESC LIMIT #{start}, #{limit}

SELECT mp_lablenum, mp_lablename, mp_lablephone, mp_bizperson, mp_bizphone, mp_useyn, mp_insertdate FROM mplan_lable
AND ${searchColumn} LIKE CONCAT('',#{searchText},'%')
ORDER BY mp_insertdate DESC LIMIT #{start}, #{limit}

